import styled from "styled-components";
import PropTypes from "prop-types";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listUnits } from "../actions/UnitAction";

function Explore() {
  const dispatch = useDispatch();

  const unitsList = useSelector((state) => state.unitsList);

  const { loading, error, units } = unitsList;

  useEffect(() => {
    dispatch(listUnits());
  }, [dispatch]);

  return (
    <>
      {loading ? (
        <div className="spinner">
          <Spin size="large" />
        </div>
      ) : error ? (
        <Alert
          type="error"
          message={error}
          showIcon
          className="alert-margin--top"
        />
      ) : (
        <>
          <Container>
            <Col span={24}>
              <h2 className="margin--top">Our Catalog of units</h2>
            </Col>
            {units.map((unit) => (
              <Col key={unit.id} sm={12} md={6} lg={4} xs={3}>
                <p>{unit.title}</p>
              </Col>
            ))}
          </Container>
        </>
      )}
    </>
  );
}

export default Explore;

const Container = styled.div`
  height: 100vh;
`;

const Col = styled.div`
  height: 3vh;
`;

const Spin = styled.div`
  margin: 80px 0;
  margin-bottom: 20px;
  padding: 30px 50px;
  text-align: center;
  background: rgba(0, 0, 0, 0.05);
  border-radius: 4px;
`;

const Alert = styled.div`
  height: 5vh;
  width: 5vh;
  margin: 80px 0;
  margin-bottom: 20px;
  padding: 30px 50px;
  text-align: center;
  background: rgba(0, 0, 0, 0.05);
  border-radius: 4px;
`;
