import { useState } from 'react'
import Header from './components/Header'
import Tasks from './components/Tasks'

const App = () =>{
    const [tasks, setTasks] = useState ([
        {
            id: 1,
            text: 'Maintenance type 1',
            day: 'today',
            reminder: true,
        },
        {
            id: 2,
            text: 'Maintenance type 2',
            day: 'today',
            reminder: true,
        },
        {
            id: 3,
            text: 'Maintenance type 3',
            day: 'today',
            reminder: true,
        },
    ])

    //Delete Task

    const deleteTask = (id) => {
      setTasks(tasks.filter((task) => task.id !== id))
    }
  //if statement impedded in the code with the > 0 ? = if : = then
  return (
    <div className='container'>
      <Header/>
      {tasks.length > 0 ? <Tasks tasks={tasks} onDelete=
      {deleteTask} /> : 'No Tasks'}
    </div>
  )
}

export default App;
